int daozhi(int n)
{
    int k = 0;
    do {
        k = 10 * k + (int)n % 10;
        n = n / 10;
    } while (n != 0);

    printf(&quot; 数字反转为：%d\n&quot;, k);
    return k;
}

void zhiheshu(int n) {
    int i, j, k = 0;
    if (n &gt; 1) {
        for (i = 2; i &lt; n; i++) {
            j = n % i;
            if (j == 0) {
                k = 0;
                break;
            }
        }
        if (i &gt;= n)k = 1;
    }
    if (k == 1 &amp;&amp; n &gt; 1) printf(&quot; %d 是素数（质数）.\n&quot;, n);
    else if (k == 0 &amp;&amp; n &gt; 1) printf(&quot; %d 是合数.\n&quot;, n);
    else printf(&quot; %d 既不是质数，也不是合数.\n&quot;, n);
}

void shuixianhuashu(int n) {
    int a[99999] = { 0 }, i = 0, j;
    int k = 0, l = n;
    while (l) {
        a[i] = (int)l % 10;
        l /= 10;
        i++;
    }
    for (j = 0; j &lt;= i - 1; j++) {
        k += (pow(a[j], i));
    }

    if (n == k) {
        switch (i) {
        case 0:
        case 1:
            printf(&quot; %d 是独身数.\n&quot;, n);
            break;
        case 3:
            printf(&quot; %d 是水仙花数.\n&quot;, n);
            break;
        case 4:
            printf(&quot; %d 是四叶玫瑰数.\n&quot;, n);
            break;
        case 5:
            printf(&quot; %d 是五角星数.\n&quot;, n);
            break;
        case 6:
            printf(&quot; %d 是六合数.\n&quot;, n);
            break;
        case 7:
            printf(&quot; %d 是北斗七星数.\n&quot;, n);
            break;
        case 8:
            printf(&quot; %d 是八仙数.\n&quot;, n);
            break;
        case 9:
            printf(&quot; %d 是九九重阳数.\n&quot;, n);
            break;
        default:
            printf(&quot;结束.\n&quot;);
            break;
        }
    }
    else if (i &gt; 9) printf(&quot; %d 可能不是水仙花数，因为它超出了整形范围.\n&quot;, n);
    else printf(&quot; %d 不是水仙花数.\n&quot;, n);
}

void wanquanshu(int n) {

    int i = n, p, q = 1, s = 0;
    for (p = 1; p &lt; i; p++) {
        q = i % p;
        if (q == 0) s = s + p;
    }
    if (s == i) {
        printf(&quot; %d 是完全数，式子为：&quot;, i);
        printf(&quot;%d = 1&quot;, i);
        for (p = 2; p &lt;= i / 2; p++) {
            q = i % p;
            if (q == 0) printf(&quot; + %d&quot;, p);
        }
        putchar('\n');
    }
    else printf(&quot; %d 不是完全数.\n&quot;, i);
}

float sum(float *score, int n)
{
    int i;
    float sum;
    sum = 0;
    for (i = 0; i &lt; n; i++)
        sum += score[i];
    printf(&quot; 其中这%d个数字的和/SUM= %.1f\n&quot;, n, sum);
    return 0;
}

float maxi(float *score, int n)
{
    int i;
    float maxi = 0;
    for (i = 0; i &lt; n; i++)
    {
        if (score[i] &gt; maxi)
            maxi = score[i];
    }
    printf(&quot; 其中这%d个数字的最大值/MAX= %.1f\n&quot;, n, maxi);
    return 0;
}

float mini(float *score, int n)
{
    int i;
    float mini = 99999;
    for (i = 0; i &lt; n; i++)
    {
        if (score[i] &lt;= mini)
