    return 0;
}

float sort1(float *score, int n)
{
    int i, j;
    float c;
    c = 0;
    for (i = 0; i &lt; n; i++)
    {
        for (j = n - 1; j &gt; i; j--)
        {
            if (score[j] &gt; score[j - 1])
            {
                c = score[j];
                score[j] = score[j - 1];
                score[j - 1] = c;
            }
        }
    }
    printf(&quot; 其中这%d个数字的由大至小排序/RANK  &quot;, n);
    for (i = 0; i &lt; n; i++)
    {
        printf(&quot;%.1f &quot;, score[i]);
    }
    putchar('\n');
    return 0;
}

