char *strcasestr(const char *str1, const char *str2) {
    char *cp = (char *)str1;
    char *s1, *s2;

    if (!*str2) return (char *)str1;

    while (*cp) {
        s1 = cp;
        s2 = (char *)str2;

        while (*s1 &amp;&amp; *s2 &amp;&amp; !(tolower(*s1) - tolower(*s2)))
            (void)(s1++), s2++;
        if (!*s2) return cp;
        cp++;
    }

    return NULL;
}

